import React, {useState} from 'react';
import './Home.css';
import myimage from './Login Page IMG.svg';

const Home = () => {
    const [open, setOpen] = useState(true);

    const Menus = [
        { title: "Dashboard", src: "Chart_fill" },
        { title: "Inbox", src: "Chat" },
        { title: "Accounts", src: "User", gap: true },
        { title: "Schedule ", src: "Calendar" },
        { title: "Search", src: "Search" },
        { title: "Analytics", src: "Chart" },
        { title: "Files ", src: "Folder", gap: true },
        { title: "Setting", src: "Setting" },
    ];

    return (
        <>
            <div className='flex'>
                <div className={`${open ? "w-72" : "w-20"} duration-300 h-screen custom-shadow-home relative p-3`}>
                    <img src="https://www.svgrepo.com/show/494008/back.svg" alt="Icon" className={`absolute cursor-pointer
                    right-[-14px] top-8 w-8 p-1 border-2 border-black rounded-full bg-white ${!open && "rotate-180"}`} onClick={()=>setOpen(!open)}/>

                    <div className='flex gap-x-4 items-center'>
                        <img src="https://i.ibb.co/HGZkXHv/Nutri-mate-logo.png" alt="Logo" className={`cursor-pointer duration-500 w-28`}/>
                        <h1 className={`ttext-black origin-left font-medium text-xl duration-300 ${!open && "scale-0"}`}>Nutri Mate</h1>
                    </div>

                    <ul className="pt-6">
                        {Menus.map((Menu, index) => (
                            <li
                            key={index}
                            className={`flex  rounded-md p-2 cursor-pointer hover:bg-light-white text-gray-500 text-sm items-center gap-x-4 
                            ${Menu.gap ? "mt-9" : "mt-2"} ${
                                index === 0 && "bg-light-white"
                            } `}
                            >

                          
                         
                            <img src={"../assets/"+Menu.src+".png"} alt="images" />
                            
                            <span className={`${!open && "hidden"} origin-left duration-200`}>
                                {Menu.title}
                            </span>

                            <img src={"../assets/"+Menu.src+".png"} alt="images" />
                            in



                        
                            </li>
                 
                           
                            
                        ))}
                         
                    </ul>
                 
                </div>
                <div className='p-7 text-2xl font-semibold flex-1 h-screen'>
                    <h1>Home Page</h1>
                </div>
                <img src={"/signup-login/src/components/Nutri-mate-logo.png"} alt="asda" />
               
               
            </div>
        </>
    );
};

export default Home;
