import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios'; // Import axios for making HTTP requests

const Dashboard = () => {
  const webcamRef = useRef(null);
  const [photoUrl, setPhotoUrl] = useState(null);

  const startWebcam = () => {
    if (webcamRef.current) {
      webcamRef.current.startCapture();
    }
  };

  const takePhoto = async () => {
    if (!webcamRef.current) {
        console.error('Webcam reference not found');
        return;
    }

    try {
        // Capture photo using webcam
        const photoDataUrl = await webcamRef.current.getScreenshot();
        
        // Update state with the captured photo URL
        setPhotoUrl(photoDataUrl);

        // Send captured photo to Flask backend
        sendPhotoToBackend(photoDataUrl);
    } catch (error) {
        console.error('Error capturing or sending photo:', error);
    }
};

const sendPhotoToBackend = async (photoDataUrl) => {
    try {
        const response = await axios.post('http://192.168.1.55:5000/detect', { photoDataUrl });
        console.log(response.data); // Log the identified object name
    } catch (error) {
        console.error('Error sending photo to backend:', error);
    }
};

  return (
    <div>
      <h1>Dashboard Page</h1>
      
      <button onClick={startWebcam}>Start Webcam</button>

      <Webcam ref={webcamRef} screenshotFormat="image/jpeg" />

      <button onClick={takePhoto} className='border-2 border-amber-400'>Take Photo</button>

      {photoUrl && (
        <div>
          <h2>Captured Photo</h2>
          <img src={photoUrl} alt="Captured" style={{ maxWidth: '100%' }} />
        </div>
      )}
    </div>
  );
};

export default Dashboard;
