import React, { useRef, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const fileInputRef = useRef(null);

  const takePhoto = async () => {
    // Trigger the file input click
    fileInputRef.current.click();
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    
    if (file) {
      // Create a FormData object to send the file
      const formData = new FormData();
      formData.append('file', file);

      // Send the uploaded file to the Python backend
      try {
        const response = await axios.post('http://127.0.0.1:5000/predict', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        // Process the response and update the UI accordingly
        console.log('Identified object:', response.data.detected_object);
      } catch (error) {
        console.error('Error sending image to Python backend:', error);
      }
    }
  };

  return (
    <div>
      <h1>Dashboard Page</h1>

      {/* File input for uploading images */}
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />

      {/* Take photo button */}
      <button onClick={takePhoto} className='border-2 border-amber-400'>Upload Image</button>
    </div>
  );
};

export default Dashboard;
